let startdate = 31d;
let enddate = 1d;
Anomalies
| where TimeGenerated > ago(enddate)
| summarize TodayCnt=count() by AnomalyTemplateName
| join (Anomalies
| where TimeGenerated > ago(startdate) and TimeGenerated < ago(enddate)
| summarize  count() by bin(TimeGenerated, 1d), AnomalyTemplateName
| summarize Maxcount=max(count_), Mincount= min(count_) by AnomalyTemplateName
//| extend AvgCnt = round(avg_count_,2)
) on AnomalyTemplateName
//| extend AnomalyRatio = round(TodayCnt/AvgCnt,2)
| extend AnomalyDetection = case(TodayCnt > Maxcount, "Spike Detected", TodayCnt < (Mincount*0.5),  "Drop Detected","No Detection")
| project AnomalyTemplateName,AnomalyDetection,TodayCnt, Maxcount,Mincount

//let Deviceaction= dynamic(["deny","Blocked","drop","block-url","drop-packet","Drop","IPS Drop"]);
let startdate = 15d;
let enddate = 1d;
CommonSecurityLog
| where TimeGenerated > ago(enddate)
| where DeviceProduct == "PAN-OS"
//|where  DeviceAction in (Deviceaction)
| summarize TodayCnt=count() by  DeviceAction
| join (CommonSecurityLog
| where TimeGenerated > ago(startdate) and TimeGenerated < ago(enddate)
| where DeviceProduct == "PAN-OS"
//|where  DeviceAction in (Deviceaction)
| summarize  count() by bin(TimeGenerated, 1d), DeviceAction
| summarize avg(count_) by DeviceAction
) on DeviceAction
| extend AvgCnt = round(avg_count_, 2)
| extend AnomalyRatio = round(TodayCnt/AvgCnt,2)
| extend AnomalyDetection = case(AnomalyRatio >2, "Spike Detected", AnomalyRatio < 0.5, "Drop Detected", "No Anomaly")
| project TodayCnt, AvgCnt, DeviceAction, AnomalyRatio, AnomalyDetection
